  font = loadFont("VT323-Regular.ttf")
}

function setup() {
  createCanvas(800, 700);

  // Set the loaded font
  textFont(font);

  // Create text input
  textInput = createInput(textString);
  textInput.position(20, height - 90);

  // Create stroke slider
  strokeSlider = createSlider(0, 10, 0, 1); // min, max, default, step
  strokeSlider.position(20, height - 60); 
  strokeSlider.class('slider');
noiseSlider = createSlider(0, 20, 1, 0.1); // min, max, default, step
  noiseSlider.position(300, height -25);
  noiseSlider.class('slider');
  // Create offset slider
  offset_slider = createSlider(-90, 90, 0, 1); // min, max, default, step
  offset_slider.position(300, height - 60);
  offset_slider.class('slider');

  // Create vertical stretching slider
  verticalStretchSlider = createSlider(1, 4.2, 1, 0.1); // min, max, default, step
  verticalStretchSlider.position(20, height - 30);
  verticalStretchSlider.class('slider');
}

function draw() {
  background(0, 209, 114);
  textString = textInput.value();

  // Sprout text size
  textSize(130);

  // Slider values
  let strokeSize = strokeSlider.value();
  let verticalStretch = verticalStretchSlider.value();
  let extraOffset = offset_slider.value();
  let noiseIntensity = noiseSlider.value();

  // Center text
  let textWidthTotal = textWidth(textString) + extraOffset * (textString.length - 1);
  let x = (width - textWidthTotal) / 2;
  let y = height - 200;
  let xOffset = 0; // Offset for each character

  // Weight and stretching
  for (let i = 0; i < textString.length; i++) {
    let char = textString.charAt(i);
    let charX = x + xOffset;
    // for (let j = 0; j < 10; j++) { // Number of noisy layers
    //   let noiseX = charX + random(-1, 1); // Random displacement
    //   let noiseY = y + random(-1, 1);
    //noiseIntensity loop from chatgpt
 for (let j = 0; j < 10; j++) {
      let noiseX = charX + random(-noiseIntensity, noiseIntensity);
      let noiseY = y + random(-noiseIntensity, noiseIntensity);
      // Draw text with noise
      push();
      translate(noiseX, noiseY);
      scale(1, verticalStretch);
      fill(255);
      noStroke();
      text(char, 0, 0);
      pop();
    }
    // Draw text with thicker stroke
    push();
    translate(charX, y);
    scale(1, verticalStretch);
    fill(255); 
    stroke(255); 
    strokeWeight(strokeSize * 2); 
    text(char, 0, 0);
    pop();

    // Draw text with thinner stroke
    push();
    translate(charX, y);
    scale(1, verticalStretch);
    fill(255);
    noStroke(); 
    text(char, 0, 0);
    pop();

    // Update xOffset for the next character
    xOffset += textWidth(char) + extraOffset;
  }

  // Text size for slider labels
  textSize(14);
  fill(0);
  let boldnessLabel = createDiv("WEIGHT");
  boldnessLabel.class('text');
  boldnessLabel.position(strokeSlider.x + strokeSlider.width + 10, strokeSlider.y);

  let offsetLabel = createDiv("OFFSET");
  offsetLabel.class('text');
  offsetLabel.position(offset_slider.x + offset_slider.width + 10, offset_slider.y);

  let stretchLabel = createDiv("STRETCH");
  stretchLabel.class('text');
  stretchLabel.position(verticalStretchSlider.x + verticalStretchSlider.width + 10, verticalStretchSlider.y );

  // At the end of the draw() function
let noiseLabel = createDiv("NOISE INTENSITY");
noiseLabel.class('text');
noiseLabel.position(noiseSlider.x + noiseSlider.width + 10, noiseSlider.y);

let inputLabel = createDiv("INPUT TEXT");
inputLabel.class('text'); inputLabel.position(textInput.x-185+textInput.width,textInput.y-25);

}

