function setup() {
  createCanvas(windowWidth, windowHeight);
  noFill();
  stroke(255);

  background(0, 81, 51);
  angleMode(DEGREES);
  myTurtle = new Turtle();
  drawFanBranches(150, 180);

  push();
  stroke(0, 255, 0);
  strokeWeight(0.05);
  for (let i = 0; i < 2000; i++) {
    let x1 = random(width);
    let y1 = random(height);
    let x2 = random(width + random(0, 200));
    let y2 = random(height + random(0, 200));
    line(x1, y1, x2, y2);
  }
  pop();
}

function drawFanBranches(length, numBranches) {

  // Draw each branch in a fan-like pattern
  for (let i = 0; i < 200; i++) {
    // Randomly choose a starting point within canvas bounds
    let startX = random(0, width);
    let startY = random(0, height);

    // Move the turtle to the starting point
    myTurtle.penUp();
    myTurtle.moveTo(startX, startY);

    // Set the angle for the current branch
    let branchAngle = random(TWO_PI / random(0,30)); // Random angle in radians

    // Draw the branch
    myTurtle.turnTo(branchAngle);
    myTurtle.penDown();
    drawBranch(length);
  }
}

function drawBranch(length) {
  if (length < random(10, 30)) {
    return;
  }
  myTurtle.moveForward(length * 0.5);
  let branchColor = color(random(0, 100), random(150, 255), random(0, 155));
  stroke(branchColor);

  // Draw this branch
  strokeWeight(0.05);
  myTurtle.moveForward(length * 0.5);
  myTurtle.turnLeft(random(100, 120));
  myTurtle.moveForward(length * 0.5);

  let circleColor = color(random(150, 250), random(0, 100), 120);

  fill(circleColor);
  noStroke();
  let circleRadius = random(0, 4);
  for (let i = 0; i < 1; i++) {
    let offsetX = random(-length / 2, length / 2);
    let offsetY = random(0, length / 2);
    ellipse(myTurtle.x + offsetX, myTurtle.y - offsetY, circleRadius * 2);
  }

  // Left child
  myTurtle.pushState();
  myTurtle.turnLeft(random(10, 30));
  drawBranch(length * 0.75);
  myTurtle.popState();

  // Right child
  myTurtle.pushState();
  myTurtle.turnRight(random(10, 15));
  drawBranch(length * 0.75);
  myTurtle.popState();
}

ArrayList<Circle> circles = new ArrayList<Circle>();

float num = 200;

int min = 1;
int max = 100;

Circle c = new Circle(new PVector(0, 0), (int)random(min, max));

void setup() {
  size(800,800);
  // noStroke();
  circles.add(c);
}

void draw() {
  background(0);
  for(int i = 0; i < circles.size(); i++){
      Circle c = (Circle) circles.get(i);
      c.draw();
  }
  PVector newLoc = new PVector(random(width/2-num, width/2+num), random(height/2-num, height/2+num));
  int newD = (int) random(min, max);
  while (detectAnyCollision (circles, newLoc, newD)) {
    /* If the values do interect make new values. */
    newLoc = new PVector(random(width/2-num, width/2+num), random(height/2-num, height/2+num));
    newD = (int) random(min, max);
  }

  c = new Circle(newLoc, newD);
  if(circles.size() < 10000){
    circles.add(c);
  }
}

static boolean detectAnyCollision(ArrayList<Circle> circles, PVector newLoc, int newR) {
  for (Circle c : circles) {
    if (c.detectCollision(newLoc, newR)) {
      return true;
    }
  }
  return false;
}


class Circle {
  PVector loc;
  int d;

  Circle(PVector loc, int d) {
    this.loc = loc;
    this.d = d;
  } 

  void draw() {
    float r = dist(loc.x, loc.y, width/2, height/2);
    float c = abs(cos(radians(r+frameCount)));
    float s = abs(sin(radians(r+frameCount)));
    fill((c-s)*10);
    if (r < num) {
      ellipse(loc.x, loc.y, d, d);
    }
  }

  boolean detectCollision(PVector newLoc, int newD) {
    return dist(loc.x, loc.y, newLoc.x, newLoc.y) < ((d + newD)/2);
  }
}

