let minOvalSize;
let maxOvalSize;
let minOvalOffset;
let maxOvalOffset;
let maxLoops;

function setup() {
  createCanvas(700, 700);
  background(255);
  numOvals = 20;
  minOvalSize = 5;
  maxOvalSize = 300;
  minOvalOffset = 10;
  maxOvalOffset = 50;
  maxLoops = 4; // Maximum number of times to go over each oval
  drawFingerprint();
}

function draw() {
  // No need for draw loop in this sketch
}

function drawFingerprint() {
  translate(width / 2, height / 2);
  for (let i = 0; i < numOvals; i++) {
    let c = color(180, 50, 50); // Set stroke color (lighter red)
    stroke(c);
    for (let j = 0; j < maxLoops; j++) {
      let ovalSize = map(i, 0, numOvals - 1, minOvalSize, maxOvalSize);
      let offset = map(i, 0, numOvals - 1, minOvalOffset, maxOvalOffset);
      let rotationOffset = random(-PI / 32, PI / 32); // Reduced randomness in orientation
      let spacingOffset = random(-5, 5);
      drawOval(0, 0, ovalSize + offset, rotationOffset, spacingOffset);
      // Randomly skip some lines
      if (random() < 0.5) {
        translate(random(-5, 5), random(-5, 5)); // Random translation
      }
    }
  }
}
function drawOval(x, y, size, rotationOffset, spacingOffset) {
  let angleStep = 0.1;
  for (let angle = 0; angle < TWO_PI; angle += angleStep) {
    let offsetX = cos(angle + rotationOffset) * size;
    let offsetY = sin(angle + rotationOffset) * size;
    let x1 = x + offsetX;
    let y1 = y + offsetY;
    // Calculate the end points of the lines along the ellipse's perimeter
    let x2 = x + cos(angle) * size;
    let y2 = y + sin(angle) * size;
    // Apply random offset to end points of the lines
    let randomX1 = x1 + random(-spacingOffset, spacingOffset);
    let randomY1 = y1 + random(-spacingOffset, spacingOffset);
    let randomX2 = x2 + random(-spacingOffset, spacingOffset);
    let randomY2 = y2 + random(-spacingOffset, spacingOffset);
    // Randomize line length
    let lineLength = dist(randomX1, randomY1, randomX2, randomY2);
    let randomLength = random(1.8, 2.4); // Increased line length
    randomX2 = randomX1 + (randomX2 - randomX1) * randomLength;
    randomY2 = randomY1 + (randomY2 - randomY1) * randomLength;
    line(randomX1, randomY1, randomX2, randomY2);
  }
}

