    circles.children[0].position = event.point;
    for (var i = 0; i < points - 1; i++) {
        var currentCircle = circles.children[i];
        var nextCircle = circles.children[i + 1];
        var vector = currentCircle.position - nextCircle.position;
        vector.length = length-i;
        nextCircle.position = currentCircle.position - vector;
    }
}

function onMouseDown(event) {
    circles.strokeColor = '#e08285';
}

function onMouseUp(event) {
    circles.strokeColor = '#e4141b';
}

