// Dot Challenge Starting Point
//things to consider:
// - middle biased
// - smaller and bigger dots
// - smaller are closer to smaller and bigger closer to bigger
// - different colors
// - blues next to blues, greens next to greens, reds next to reds

function setup() {
  createCanvas(400, 400);
}

function draw() {
  background(50);

  noStroke();
  ellipseMode(CENTER);

  let frequency = 0.02;
  let circleCount = 100

  for (let i = 0; i < circleCount; i++) {

  let inter = map(sin(i * 0.01), -1, 1, 0, 1);
  let circleColor;
    if(i < circleCount/3){
      circleColor = lerpColor(color("#0000ff"), color("#FF00CE"), inter);

    } else if( i == circleCount/3 || i < 2 * circleCount/3) {
      circleColor = lerpColor(color("#00FF00"), color("#0000ff"), inter);  
    } else {
      circleColor = lerpColor(color("#FF0000"), color("#FFE500"), inter);
    }



    let w = random(width / 8, width) + random(width / 8, width) / 1.5;
    let h = (random(height / 8, height) + random(height / 8, height)) / 1.5;
    let x = noise(i * frequency) * w;
    let y = noise(i * frequency) * h;

    // the diameter shouldn't always be 10, it needs to varyfrequency
    let diameter = noise(i * frequency) * 20;

    // the colors also need to change
    // what colorMode would be easiest to work with?
    fill(circleColor);

    ellipse(x, y, diameter, diameter);
  }

  noLoop();
}

let img;

function setup() {
  createCanvas(600, 400);
  img = loadImage('venus.jpg', imageLoaded);
}

function imageLoaded() {
  image(img, 0, 0, 600, 400);
  loadPixels(); 

  let pixelSize = 6; 

  //process pixel
  for (let y = 2; y < 400; y += pixelSize) {
    for (let x = 2; x < 600; x += pixelSize) {
      //calculate index position in pixel array
      let index = (x + y * img.width) * 4;
      //getcolor-current pixel
      let r = pixels[index];
      let g = pixels[index + 1];
      let b = pixels[index + 2];
      //fill color 
      fill(r, g, b);

      ellipse(x, y, pixelSize, pixelSize);
    }
  }
}

