    stroke(random(30, 60), random(70, 100), random(5, 45));
    myTurtle.moveForward(23);
    myTurtle.turnRight(2+i);
  }
  myTurtle.penDown();
  for (let i = 200; i < 350; i++) {
    stroke(random(30, 60), random(70, 100), random(5, 45));
    myTurtle.moveForward(23);
    myTurtle.turnRight(2+i);
  }

  myTurtle.penUp();
  myTurtle.moveTo(170, 170);
  strokeWeight(2);
  for (let i = 0; i < 200; i++) {
    stroke(random(30, 60), random(70, 100), random(5, 45));
    myTurtle.moveForward(23);
    myTurtle.turnRight(2+i);
  }
  myTurtle.penDown();
  for (let i = 200; i < 350; i++) {
    stroke(random(30, 60), random(70, 100), random(5, 45));
    myTurtle.moveForward(23);
    myTurtle.turnRight(2+i);
  }


  // draw a flower
  myTurtle.penUp();
  myTurtle.moveTo(250, 250);
  myTurtle.penDown();

  stroke(250, 170, 200);
  strokeWeight(2);

  //drawing many flowers
  for (let h = 0; h < 40; h++) {
    x = random(500);
    y = random(500);
    myTurtle.penUp();
    myTurtle.moveTo(x, y);
    myTurtle.penDown();
    //petals
    stroke(250, random(177, 240),230)
    petals = floor(random(4, 11));
    let angleIncrement = map(petals, 4, 10, 12, 7); 
    let segments = map(angleIncrement, 7, 12, 35, 19);
    for (let j = 0; j < petals; j++) {
      myTurtle.moveForward(10);
      for (let i = 0; i < segments; i++) {
        myTurtle.turnRight(angleIncrement);
        myTurtle.moveForward(1);
      }

      myTurtle.moveForward(8);
      myTurtle.turnRight(180);
    }
    // center of flower
    fill(237, 225, 133);
    ellipse(x, y, 10);
    noFill();

  }




  noLoop();
}

