//wave noise left to right
function waveMaker(){
  this.xoff = 0; /// noise parameter value
  this.yoff = 0; /// noise parameter value
  this.yend = 0; /// used as the last vertex
  this.ymod = -height/4; /// y-position
  this.dense = width/15; /// step 
  this.px = 0; /// shrub
  this.py = 0; /// shrub
  this.size = 0; 
  this.shrubs = 1; 

  this.update = function() {
    //// First Layer Wave
    push();

    beginShape();
    vertex(-width/2,map(noise(0,this.yoff),0,1,
                  -height*0.25,height*0.25));
    let randomPoint = int(random(-width/2, width/2));
  let yForX = map(noise(this.xoff, this.yoff), 0, 1, -height*0.25, height*0.25);


    this.xoff = 0;

    this.dense = map(posy,0,height,15,25);


    for (let x = -width/2; x < width/2;
         x += this.dense){


      let xmod = map(noise(this.yoff,x),0,1,
                     -this.dense,this.dense);


      let y = map(noise(this.xoff,this.yoff),0,1,
                  -height*0.25,height*0.25);

      let sw = map(noise(this.xoff,this.yoff),0,1,
                   2,20);



      this.size = map(posy,0,height,2,25);
      vertex(x+xmod,y);


      stroke(0,128,0,128);
      strokeWeight(this.size);


      if (random() < this.shrubs){
        this.madrone(x+xmod,y);
      }

      let tree = random(this.size*0.01,
                          this.size*0.5);

      if (random() < this.shrubs*0.002) { /// chance of shrubs
        push();

        translate(x+xmod+random(-this.size*2,this.size*2),y);
        rotate(random(-PI*0.02,PI*0.02));


        if (random() < 0.1){ /// chance of dead tree
          let qp = HALF_PI; /// upright orientation
          if (random() < 0.15) { /// chance of felling
            qp = -HALF_PI; /// fell orientation
          }
          rotate(qp+random(-PI*0.02,PI*0.02));
        }


        stroke(25,random(25,155),15,255);
        strokeWeight(this.size*0.15);
        line(0,0,0,-this.size*2);
        pop();

        /// tree types.
      } else if (random() < 0.1) { /// chance of green trees
        push();
        stroke(0,50+random(0,100),25,255); 
//         if (random() < 0.05) { /// chance of brown trees
//           stroke(random(75,125),50,10,255); 
//         }


        translate(x+xmod,y);
        triangle(0,random(-this.size*0.15,0)-tree,
                 -tree,tree,
                 tree,tree);
        pop();
      }

      this.xoff += 0.05;


      this.yend = y;
    }

    vertex(width/2,this.yend);


    for (let x = width/2; x > -width/2;
         x -= this.dense){
      let xmod = map(noise(this.yoff,x),0,1,
                     -this.dense,this.dense);
      let y = map(noise(this.xoff,this.yoff),0,1,
                  -height*0.25,height*0.25);
      let sw = map(noise(this.xoff,this.yoff),0,1,
                   2,12);
      let shapesize = map(posy,0,height,2,25);
      stroke(0,255-random(128),0,255);
      strokeWeight(this.size);
      vertex(x+xmod,y);

      if (random() < this.shrubs*0.5){
        // this.madrone(x+xmod,y);
      }

      /// /evergreen
      let tree = random(this.size*0.1,
                          this.size*0.5);
      if (random() < 0.075) { /// chance of green grass
        push();
        stroke(0,random(175,50),25,255);
        // if (random() < 0.02) { /// chance of brown grass
        //   stroke(125,50,10,255);
        // }
        // translate(x+xmod,y);
        // strokeWeight(1);

        pop();
      }

      this.xoff += 0.05;
      this.yend = y;
    }
    stroke(0,0,0,40);
    strokeWeight(1);

    if (frameCount%32 === 0) {
      strokeWeight(2);
    }


    let rsub = random(240,255);
    fill(rsub,rsub,255,noise(1,0.59)*100);
    vertex(-width/2,this.yend);
    endShape(CLOSE);
    // chatgpt: draw the "X" on the terrain
    if (!xDrawn) {
      let randomX = random(0, width);
      let yForX = map(noise(this.xoff, randomX * 0.01), 0, 1, height * 0.25, height * 0.75) + posy; //draw 1/4 to 3/4 canvas height 
      drawX(randomX, yForX, 20);
      xDrawn = true; //set to true after x is drawn
    }

    pop();

    this.yoff += 0.01;

  }

  this.madrone = function(tx,ty){
    push();
    translate(tx,ty);
    for (let q = 0; q < 1; q++){ /// density of cluster
      this.px = random(this.size*0.05); /// range
      rotate(random(TWO_PI)); /// radial location
      strokeWeight(this.size*random(0.1,0.5));
      stroke(random(0,128),random(50,255),0,255);
      this.py = random(-this.size*0.25,this.size*0.25);
      point(this.px,this.py);
    }
    pop();
  }
